import React, { useState, useEffect } from "react";
import { listarProdutos, criarProduto, deletarProduto } from "./api";

function App() {
  const [produtos, setProdutos] = useState([]);
  const [nome, setNome] = useState("");
  const [preco, setPreco] = useState("");
  const [quantidade, setQuantidade] = useState("");
  const [foto, setFoto] = useState(null);

  useEffect(() => {
    loadProdutos();
  }, []);

  const loadProdutos = async () => {
    const data = await listarProdutos();
    setProdutos(data);
  };

  const handleAddProduto = async () => {
    if (!nome || !preco || !quantidade) return;

    const formData = new FormData();
    formData.append("nome", nome);
    formData.append("preco", preco);
    formData.append("quantidade", quantidade);
    if (foto) formData.append("foto", foto);

    await criarProduto(formData);
    setNome(""); setPreco(""); setQuantidade(""); setFoto(null);
    loadProdutos();
  };

  const handleDelete = async (id) => {
    await deletarProduto(id);
    loadProdutos();
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h1 className="text-4xl font-bold text-blue-600 mb-8">Sistema de Gestão de Loja</h1>

      <div className="w-full max-w-5xl bg-white p-6 rounded-xl shadow-lg mb-8">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">Adicionar Produto</h2>
        <div className="flex flex-col md:flex-row gap-4">
          <input
            className="border border-gray-300 p-2 rounded flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Nome"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
          />
          <input
            className="border border-gray-300 p-2 rounded w-32 focus:outline-none focus:ring-2 focus:ring-blue-400"
            type="number"
            placeholder="Preço"
            value={preco}
            onChange={(e) => setPreco(e.target.value)}
          />
          <input
            className="border border-gray-300 p-2 rounded w-32 focus:outline-none focus:ring-2 focus:ring-blue-400"
            type="number"
            placeholder="Quantidade"
            value={quantidade}
            onChange={(e) => setQuantidade(e.target.value)}
          />
          <input
            type="file"
            onChange={(e) => setFoto(e.target.files[0])}
            className="border border-gray-300 p-2 rounded"
          />
          <button
            className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition duration-300 shadow"
            onClick={handleAddProduto}
          >
            Adicionar
          </button>
        </div>
      </div>

      <div className="w-full max-w-5xl bg-white p-6 rounded-xl shadow-lg">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">Produtos Cadastrados</h2>
        <table className="w-full table-auto border-collapse">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2 text-left">ID</th>
              <th className="border px-4 py-2 text-left">Nome</th>
              <th className="border px-4 py-2 text-left">Preço</th>
              <th className="border px-4 py-2 text-left">Quantidade</th>
              <th className="border px-4 py-2 text-left">Foto</th>
              <th className="border px-4 py-2 text-left">Ações</th>
            </tr>
          </thead>
          <tbody>
            {produtos.map((p) => (
              <tr key={p.id} className="hover:bg-gray-100 transition">
                <td className="border px-4 py-2">{p.id}</td>
                <td className="border px-4 py-2">{p.nome}</td>
                <td className="border px-4 py-2">R$ {p.preco_venda}</td>
                <td className="border px-4 py-2">{p.quantidade}</td>
                <td className="border px-4 py-2">
                  {p.foto && (
                    <img
                      src={`http://localhost:8000/fotos_produtos/${p.foto}`}
                      alt={p.nome}
                      className="w-16 h-16 object-cover rounded-lg"
                    />
                  )}
                </td>
                <td className="border px-4 py-2">
                  <button
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 transition duration-300 shadow"
                    onClick={() => handleDelete(p.id)}
                  >
                    Deletar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
