import React, { useState, useEffect } from "react";
import { listarProdutos, criarProduto, deletarProduto } from "./api";

function App() {
  const [produtos, setProdutos] = useState([]);
  const [nome, setNome] = useState("");
  const [preco, setPreco] = useState("");
  const [quantidade, setQuantidade] = useState("");
  const [foto, setFoto] = useState(null);

  useEffect(() => { loadProdutos(); }, []);

  const loadProdutos = async () => {
    const data = await listarProdutos();
    setProdutos(data);
  };

  const handleAddProduto = async () => {
    if (!nome || !preco || !quantidade) return;
    const formData = new FormData();
    formData.append("nome", nome);
    formData.append("preco", preco);
    formData.append("quantidade", quantidade);
    if (foto) formData.append("foto", foto);
    await criarProduto(formData);
    setNome(""); setPreco(""); setQuantidade(""); setFoto(null);
    loadProdutos();
  };

  const handleDelete = async (id) => {
    await deletarProduto(id);
    loadProdutos();
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h1 className="text-4xl font-bold text-blue-600 mb-8">Gestão de Loja</h1>

      {/* Formulário de adicionar produto */}
      <div className="w-full max-w-5xl bg-white p-6 rounded-xl shadow-lg mb-8">
        <div className="flex flex-col md:flex-row gap-4 items-center">
          
          {/* Inputs */}
          <input
            placeholder="Nome"
            value={nome}
            onChange={e => setNome(e.target.value)}
            className="border rounded px-3 py-2 focus:border-blue-500 focus:ring focus:ring-blue-200 flex-1"
          />
          <input
            type="number"
            placeholder="Preço"
            value={preco}
            onChange={e => setPreco(e.target.value)}
            className="border rounded px-3 py-2 focus:border-blue-500 focus:ring focus:ring-blue-200 w-32"
          />
          <input
            type="number"
            placeholder="Quantidade"
            value={quantidade}
            onChange={e => setQuantidade(e.target.value)}
            className="border rounded px-3 py-2 focus:border-blue-500 focus:ring focus:ring-blue-200 w-24"
          />

          {/* Botão de escolher foto estilizado */}
          <div className="flex items-center gap-2">
            <label
              htmlFor="foto"
              className="cursor-pointer bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-all font-semibold"
            >
              Escolher Foto
            </label>
            <span className="text-gray-600">{foto ? foto.name : "Nenhum arquivo selecionado"}</span>
            <input
              id="foto"
              type="file"
              className="hidden"
              onChange={(e) => setFoto(e.target.files[0])}
            />
          </div>

          {/* Botão adicionar */}
          <button
            onClick={handleAddProduto}
            className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-all font-semibold"
          >
            Adicionar
          </button>
        </div>
      </div>

      {/* Tabela de produtos */}
      <table className="w-full max-w-5xl bg-white rounded-xl shadow-lg overflow-hidden">
        <thead className="bg-blue-500 text-white">
          <tr>
            <th className="px-4 py-2">ID</th>
            <th className="px-4 py-2">Nome</th>
            <th className="px-4 py-2">Preço</th>
            <th className="px-4 py-2">Qtd</th>
            <th className="px-4 py-2">Foto</th>
            <th className="px-4 py-2">Ações</th>
          </tr>
        </thead>
        <tbody>
          {produtos.map(p => (
            <tr key={p.id} className="border-b hover:bg-gray-100 transition-colors">
              <td className="px-4 py-2">{p.id}</td>
              <td className="px-4 py-2">{p.nome}</td>
              <td className="px-4 py-2">{p.preco_venda}</td>
              <td className="px-4 py-2">{p.quantidade}</td>
              <td className="px-4 py-2">
                {p.foto && <img src={`http://localhost:8000/fotos_produtos/${p.foto}`} className="w-16 h-16 rounded-lg object-cover" />}
              </td>
              <td className="px-4 py-2">
                <button
                  onClick={() => handleDelete(p.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded-lg hover:bg-red-600 transition-all font-semibold"
                >
                  Deletar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
