import React, { useState, useEffect } from "react";
import { listarProdutos, criarProduto, deletarProduto } from "./api";

function App() {
  const [produtos, setProdutos] = useState([]);
  const [nome, setNome] = useState("");
  const [preco, setPreco] = useState("");
  const [quantidade, setQuantidade] = useState("");
  const [foto, setFoto] = useState(null);

  useEffect(() => { loadProdutos(); }, []);

  const loadProdutos = async () => {
    const data = await listarProdutos();
    setProdutos(data);
  };

  const handleAddProduto = async () => {
    if (!nome || !preco || !quantidade) return;
    const formData = new FormData();
    formData.append("nome", nome);
    formData.append("preco", preco);
    formData.append("quantidade", quantidade);
    if (foto) formData.append("foto", foto);
    await criarProduto(formData);
    setNome(""); setPreco(""); setQuantidade(""); setFoto(null);
    loadProdutos();
  };

  const handleDelete = async (id) => {
    await deletarProduto(id);
    loadProdutos();
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h1 className="text-4xl font-bold text-blue-600 mb-8">Gestão de Loja</h1>

      <div className="w-full max-w-5xl bg-white p-6 rounded-xl shadow-lg mb-8">
        <div className="flex flex-col md:flex-row gap-4">
          <input placeholder="Nome" value={nome} onChange={e=>setNome(e.target.value)} />
          <input type="number" placeholder="Preço" value={preco} onChange={e=>setPreco(e.target.value)} />
          <input type="number" placeholder="Quantidade" value={quantidade} onChange={e=>setQuantidade(e.target.value)} />
          <input id="enviar_" type="file" onChange={e=>setFoto(e.target.files[0])} />
          <button onClick={handleAddProduto}>Adicionar</button>
        </div>
      </div>

      <table>
        <thead>
          <tr><th>ID</th><th>Nome</th><th>Preço</th><th>Qtd</th><th>Foto</th><th>Ações</th></tr>
        </thead>
        <tbody>
          {produtos.map(p => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.nome}</td>
              <td>{p.preco_venda}</td>
              <td>{p.quantidade}</td>
              <td>{p.foto && <img src={`http://localhost:8000/fotos_produtos/${p.foto}`} width="50" />}</td>
              <td><button onClick={()=>handleDelete(p.id)}>Deletar</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
