import React from "react";

export default function Reset() {
  const handleReset = async () => {
    if (!window.confirm("Deseja realmente resetar todos os produtos?")) return;
    await fetch("http://localhost:8000/produtos/reset", { method: "POST" });
    alert("Produtos resetados!");
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-6">
      <h1 className="text-4xl font-bold text-red-600 mb-8">Resetar Produtos</h1>
      <button onClick={handleReset} className="bg-red-600 text-white px-6 py-3 rounded hover:bg-red-700 transition">
        Resetar Todos os Produtos
      </button>
    </div>
  );
}
