import React from "react";
import { useNavigate } from "react-router-dom";

export default function Reset() {
  const navigate = useNavigate();

  const handleReset = async () => {
    if (!window.confirm("Deseja realmente resetar todos os produtos?")) return;
    await fetch("http://localhost:8000/produtos/reset", { method: "POST" });
    alert("Produtos resetados!");
    navigate("/produtos");
  };

  return (
    <div className="p-6 text-center">
      <h1 className="text-2xl font-bold mb-4 text-red-600">Resetar Produtos</h1>
      <button onClick={handleReset} className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700">
        Resetar Todos
      </button>
    </div>
  );
}
