import axios from 'axios';

const API_URL = "http://127.0.0.1:8000";

export const listarProdutos = async () => {
    const res = await axios.get(`${API_URL}/produtos/`);
    return res.data;
}

export const criarProduto = async (produto, fotoFile) => {
    const formData = new FormData();
    for (const key in produto) formData.append(key, produto[key]);
    if (fotoFile) formData.append('foto', fotoFile);
    const res = await axios.post(`${API_URL}/produtos/`, formData);
    return res.data;
}

export const deletarProduto = async (id) => {
    const res = await axios.delete(`${API_URL}/produtos/${id}`);
    return res.data;
}

export const registrarVenda = async (itens, tipo_pagamento) => {
    const res = await axios.post(`${API_URL}/vendas/`, { itens, tipo_pagamento });
    return res.data;
}
