import React, { useState, useEffect } from 'react';
import { listarProdutos, criarProduto, deletarProduto } from './api';

function App() {
  const [produtos, setProdutos] = useState([]);
  const [nome, setNome] = useState('');
  const [preco, setPreco] = useState('');
  const [quantidade, setQuantidade] = useState('');
  const [foto, setFoto] = useState(null);

  useEffect(() => {
    loadProdutos();
  }, []);

  const loadProdutos = async () => {
    const data = await listarProdutos();
    setProdutos(data);
  }

  const handleAddProduto = async () => {
    if (!nome || !preco || !quantidade) return;
    await criarProduto({ nome, preco_venda: preco, quantidade }, foto);
    setNome(''); setPreco(''); setQuantidade(''); setFoto(null);
    loadProdutos();
  }

  const handleDelete = async (id) => {
    await deletarProduto(id);
    loadProdutos();
  }

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold text-blue-600 mb-6">Gestão de Loja</h1>

      {/* Formulário */}
      <div className="w-full max-w-4xl bg-white p-6 rounded shadow mb-6">
        <h2 className="text-xl font-semibold mb-4">Adicionar Produto</h2>
        <div className="flex flex-col md:flex-row gap-4">
          <input className="border p-2 rounded flex-1" placeholder="Nome" value={nome} onChange={e => setNome(e.target.value)} />
          <input className="border p-2 rounded w-32" type="number" placeholder="Preço" value={preco} onChange={e => setPreco(e.target.value)} />
          <input className="border p-2 rounded w-32" type="number" placeholder="Quantidade" value={quantidade} onChange={e => setQuantidade(e.target.value)} />
          <input type="file" onChange={e => setFoto(e.target.files[0])} />
          <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition" onClick={handleAddProduto}>
            Adicionar
          </button>
        </div>
      </div>

      {/* Tabela de produtos */}
      <div className="w-full max-w-4xl bg-white p-6 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Produtos</h2>
        <table className="w-full table-auto border-collapse">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2">ID</th>
              <th className="border px-4 py-2">Nome</th>
              <th className="border px-4 py-2">Preço</th>
              <th className="border px-4 py-2">Quantidade</th>
              <th className="border px-4 py-2">Foto</th>
              <th className="border px-4 py-2">Ações</th>
            </tr>
          </thead>
          <tbody>
            {produtos.map(p => (
              <tr key={p.id} className="hover:bg-gray-100">
                <td className="border px-4 py-2">{p.id}</td>
                <td className="border px-4 py-2">{p.nome}</td>
                <td className="border px-4 py-2">R$ {p.preco_venda}</td>
                <td className="border px-4 py-2">{p.quantidade}</td>
                <td className="border px-4 py-2">
                  {p.foto && <img src={`http://localhost:8000/fotos_produtos/${p.foto}`} alt={p.nome} className="w-16 h-16 object-cover rounded" />}
                </td>
                <td className="border px-4 py-2">
                  <button className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600 transition" onClick={() => handleDelete(p.id)}>Deletar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
